using System;\nusing System.Collections;\n\npublic class HashCodeUtil\n{\n    public static int GetHashCodeOfList(ArrayList list)\n    {\n        unchecked // allow overflow\n        {\n            int hash = 17; // initialize with a non-zero constant\n\n            foreach (string str in list)\n            {\n                if (str != null) // avoid null reference exceptions\n                {\n                    hash ^= str.GetHashCode(); // xor current hash with string's hash code\n                }\n            }\n\n            return hash;\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        ArrayList list1 = new ArrayList();\n        list1.Add("String1");\n        list1.Add("String2");\n        list1.Add("String3");\n\n        ArrayList list2 = new ArrayList();\n        list2.Add("String3");\n        list2.Add("String2");\n        list2.Add("String1");\n\n        Console.WriteLine(HashCodeUtil.GetHashCodeOfList(list1)); // should be equal\n        Console.WriteLine(HashCodeUtil.GetHashCodeOfList(list2)); // should be equal\n    }\n}