static List<List<string>> ReadFromFile(string filename)\n{\n    using (StreamReader SR = File.OpenText(filename))\n    {\n        List<List<string>> matrix = new List<List<string>>();\n        string line;\n\n        while ((line = SR.ReadLine()) != null)\n        {\n            // Create a new list for each row\n            List<string> row = new List<string>();\n            \n            // Split the line by spaces and add to the row\n            string[] elements = line.Split(' ', StringSplitOptions.RemoveEmptyEntries);\n            foreach (string element in elements)\n            {\n                row.Add(element);\n            }\n\n            // Add the populated row to the matrix\n            matrix.Add(row);\n        }\n        \n        // Return the matrix with all rows\n        return matrix;\n    }\n}