protected void btnFinish_Click(object sender, EventArgs e)\n{\n    // Perform validation of all validators regardless of visibility\n    bool isValid = true;\n    \n    foreach (BaseValidator validator in Page.Validators)\n    {\n        if (!validator.IsValid)\n        {\n            isValid = false;\n        }\n    }\n\n    // Check the overall page validity and perform actions accordingly\n    if (isValid)\n    {\n        // Proceed with processing\n    }\n    else\n    {\n        // Show validation summary or notify user\n        ValidationSummary1.Visible = true; // Ensure the summary is visible\n    }\n}