// param is a string like 'parametername:value'\nstring[] parts = param.Split(':');\nif (parts.Length != 2)\n{\n    throw new ArgumentException("Parameter format is incorrect.");\n}\n\nstring parameterName = parts[0].Trim();\nstring parameterValue = parts[1].Trim();\n\n// repDoc is the current Report\nif (!repDoc.ParameterFields.ContainsKey(parameterName))\n{\n    throw new ArgumentException($"Report does not contain a parameter named '{parameterName}'.");\n}\n\n// Set parameter value using ParameterDiscreteValue\nParameterField paramField = repDoc.ParameterFields[parameterName];\nParameterDiscreteValue discreteVal = new ParameterDiscreteValue();\ndiscreteVal.Value = parameterValue;\nparamField.CurrentValues.Clear();\nparamField.CurrentValues.Add(discreteVal);\n\n// Now export the report to HTTP response as PDF\nrepDoc.ExportToHttpResponse(ExportFormatType.PortableDocFormat, response, false, "Report");