// Assuming your BindingSource is named bindingSource and DataGridView is named dataGridView\n\npublic Form1()\n{\n    InitializeComponent();\n    \n    // Subscribe to the ListChanged event\n    bindingSource.ListChanged += BindingSource_ListChanged;\n}\n\nprivate bool _hasChanges = false;\n\nprivate void BindingSource_ListChanged(object sender, ListChangedEventArgs e)\n{\n    if (e.ListChangedType != ListChangedType.Reset)\n    {\n        _hasChanges = true;\n    }\n}\n\n// Method to check for changes\nprivate bool HasUnsavedChanges()\n{\n    return _hasChanges;\n}\n\nprivate void SaveButton_Click(object sender, EventArgs e)\n{\n    if (_hasChanges)\n    {\n        // Ask user to confirm changes\n        DialogResult result = MessageBox.Show("Do you want to save your changes?", "Save Changes", MessageBoxButtons.YesNo);\n        if (result == DialogResult.Yes)\n        {\n            // Code to save data\n            SaveData();\n            _hasChanges = false; // Reset the flag after saving\n        }\n    }\n}\n\nprivate void SaveData()\n{\n    // Implement your logic to save data to the database here\n}