using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = @"\n<env:Envelope xmlns:env='http://schemas.xmlsoap.org/soap/envelope/'\n              xmlns:xsd='http://www.w3.org/2001/XMLSchema'\n              xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance'\n              xmlns:ns0='http://dev1/MyWebService1.wsdl'>\n    <env:Header>\n        <wsse:Security xmlns:wsse='http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd'\n                       xmlns='http://docs.oasis-open.org/wss/2004/01/oasis-200401-wss-wssecurity-secext-1.0.xsd'\n                       env:mustUnderstand='1'/>\n    </env:Header>\n    <env:Body>\n        <ns0:getItemNbrByItemIdResponseElement>\n            <ns0:result>1010603</ns0:result>\n        </ns0:getItemNbrByItemIdResponseElement>\n    </env:Body>\n</env:Envelope>";\n\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xml);\n\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(xmlDoc.NameTable);\n        nsmgr.AddNamespace("env", "http://schemas.xmlsoap.org/soap/envelope/");\n        nsmgr.AddNamespace("ns0", "http://dev1/MyWebService1.wsdl");\n\n        XPathNavigator nav = xmlDoc.CreateNavigator();\n        string xpath = "//ns0:getItemNbrByItemIdResponseElement/ns0:result";\n        nav.MoveToFollowing(XPathNodeType.Element);\n        nodeIterator iter = nav.Select(xpath, nsmgr);\n\n        if (iter.MoveNext())\n        {\n            Console.WriteLine("Item Number: " + iter.Current.Value);\n        }\n    }\n}