using System;\nusing Microsoft.CodeAnalysis.CSharp.Scripting;\nusing Microsoft.CodeAnalysis.Scripting;\n\nclass Program\n{\n    static void Main()\n    {\n        string code = @"\n            int result = 42 + 13;\n            return result;";\n\n        // Compile and run the given C# script\n        var state = CSharpScript.RunAsync(code).Result;\n        Console.WriteLine("Execution Result: " + state.ReturnValue);\n    }\n}