using System.Threading.Tasks;\nusing SendGrid;\nusing SendGrid.Helpers.Mail;\n\npublic class EmailSender\n{\n    public static async Task SendEmailAsync(string apiKey, string fromEmail, string toEmail, string subject, string content)\n    {\n        var client = new SendGridClient(apiKey);\n        var msg = MailHelper.CreateSingleEmail(new EmailAddress(fromEmail), new EmailAddress(toEmail), subject, "", content);\n        var response = await client.SendEmailAsync(msg);\n\n        Console.WriteLine("Status Code: " + (int)response.StatusCode);\n        Console.WriteLine("Body: " + response.Body.ReadAsStringAsync().Result);\n    }\n}