protected void Repeater1_ItemDataBound(object sender, RepeaterItemEventArgs e)\n{\n    if (e.Item.ItemType == ListItemType.Item || e.Item.ItemType == ListItemType.AlternatingItem)\n    {\n        // Assume data item is a DataRowView\n        DataRowView row = (DataRowView)e.Item.DataItem;\n\n        // Find the ThumbViewer control\n        var thumbViewer = e.Item.FindControl("Th1") as Bri.ThumbViewer;\n\n        if (thumbViewer != null)\n        {\n            // Set ImageUrl based on your logic or data binding expression\n            thumbViewer.ImageUrl = "images/" + row["Name"].ToString();\n        }\n    }\n}