var cacheKey = $"UserProfile_{userId}";\n   UserProfile userProfile;\n   if (HttpContext.Current.Cache[cacheKey] == null)\n   {\n       userProfile = GetUserProfileFromDatabase(userId); // Fetch from DB or other source\n       HttpContext.Current.Cache.Insert(cacheKey, userProfile, null, DateTime.Now.AddHours(1), TimeSpan.Zero);\n   }\n   else\n   {\n       userProfile = (UserProfile)HttpContext.Current.Cache[cacheKey];\n   }