using Microsoft.Win32;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string iisVersion = GetIISVersion();\n        if (iisVersion != null)\n        {\n            Console.WriteLine("Installed IIS Version: " + iisVersion);\n        }\n        else\n        {\n            Console.WriteLine("IIS is not installed or version could not be determined.");\n        }\n    }\n\n    static string GetIISVersion()\n    {\n        try\n        {\n            using (RegistryKey iisPath = Registry.LocalMachine.OpenSubKey(@"SOFTWARE\Microsoft\InetStp"))\n            {\n                if (iisPath != null)\n                {\n                    object version = iisPath.GetValue("MajorVersion");\n                    if (version != null)\n                    {\n                        return version.ToString();\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error accessing registry: " + ex.Message);\n        }\n\n        return null;\n    }\n}