// Example with IDistributedCache (Redis or In-Memory)\n   var cacheKey = $"UserProfile_{userId}";\n   if (!_cache.TryGetValue<UserProfile>(cacheKey, out UserProfile userProfile))\n   {\n       userProfile = GetUserProfileFromDatabase(userId);\n       await _cache.SetAsync(cacheKey, userProfile, new DistributedCacheEntryOptions().SetSlidingExpiration(TimeSpan.FromHours(1)));\n   }