using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic class OrderService\n{\n    private readonly HttpClient _httpClient = new HttpClient();\n    private const string ApiUrl = "https://api.microssystem.com/orders"; // Replace with actual API URL\n\n    public async Task SendOrderAsync(Order order)\n    {\n        var jsonContent = Newtonsoft.Json.JsonConvert.SerializeObject(order); // Convert your Order object to JSON\n        using (var content = new StringContent(jsonContent, Encoding.UTF8, "application/json"))\n        {\n            var response = await _httpClient.PostAsync(ApiUrl, content);\n            if (!response.IsSuccessStatusCode)\n            {\n                throw new Exception($"Failed to send order. Status code: {response.StatusCode}");\n            }\n        }\n    }\n}\n\npublic class Order\n{\n    public int Id { get; set; }\n    public string CustomerName { get; set; }\n    public List<OrderItem> Items { get; set; } // Assuming you have an OrderItem class to represent items in the order\n}