using System.Collections.Generic;\n\npublic class FileTypeChecker\n{\n    private static Dictionary<string, List<byte[]>> _fileSignature = new Dictionary<string, List<byte[]>>\n    {\n        { ".pdf", new List<byte[]> { new byte[] { 0x25, 0x50, 0x44, 0x46 } } },\n        // Add others as necessary\n    };\n\n    public bool IsFileValid(string extension, byte[] fileBytes)\n    {\n        if (_fileSignature.TryGetValue(extension.ToLower(), out var signatures))\n        {\n            foreach (var signature in signatures)\n            {\n                if (fileBytes.Length >= signature.Length && !signature.Where((t, i) => t != fileBytes[i]).Any())\n                    return true;\n            }\n        }\n\n        return false;\n    }\n}