using System.IO;\nusing Syncfusion.Pdf;\nusing Syncfusion.Pdf.Graphics;\n\npublic void CreatePdf(string dest)\n{\n    // Create a new PDF document.\n    PdfDocument document = new PdfDocument();\n\n    // Add a page to the PDF document.\n    PdfPage page = document.Pages.Add();\n    \n    // Create PDF graphics for the page.\n    PdfGraphics graphics = page.Graphics;\n    \n    // Set standard font.\n    PdfStandardFont font = new PdfStandardFont(PdfFontFamily.Helvetica, 12);\n\n    // Draw a string in the specified location.\n    graphics.DrawString("Hello, this is a dynamically generated PDF!", font, PdfBrushes.Black, new PointF(0, 0));\n\n    // Save the document to file stream.\n    using (FileStream fs = File.Create(dest))\n        document.Save(fs);\n}