using System;\nusing System.Messaging;\n\npublic class MsmqMessageDeleter\n{\n    public void DeleteSpecificMessage(string queuePath, string messageId)\n    {\n        using (MessageQueue queue = new MessageQueue(queuePath))\n        {\n            // Set the message lookup criteria\n            MessageEnumerator enumerator = queue.GetMessageEnumerator2();\n            \n            while (enumerator.MoveNext())\n            {\n                Message currentMessage = enumerator.Current;\n                \n                if (currentMessage.Id == messageId)\n                {\n                    // Move the cursor to the message and delete it\n                    enumerator.RemoveCurrent();\n                    break;\n                }\n            }\n        }\n    }\n}\n\n// Usage example:\nclass Program\n{\n    static void Main()\n    {\n        MsmqMessageDeleter deleter = new MsmqMessageDeleter();\n        string queuePath = @".\Private$\YourQueueName";\n        string messageIdToDelete = "specific-message-id";\n        \n        deleter.DeleteSpecificMessage(queuePath, messageIdToDelete);\n    }\n}