var result = (from item in myTable\n              group item by item.title into g\n              select new \n              { \n                  Title = g.Key,\n                  F1Count = g.Count(x => x.f1 == 1),\n                  F2Count = g.Count(x => x.f2 == 1),\n                  F3Count = g.Count(x => x.f3 == 1)\n              }).ToList();