static void Main()\n{\n    string str;\n    str = Console.ReadLine();\n    while (str != null && str.Length > 0) // Check for non-null and non-empty string\n    {\n        str = str.Remove(0, 1);\n        if (str.Length > 0) // Ensure the string is not empty after removing characters\n        {\n            str = str.Remove(str.Length - 1, 1);\n            string[] value = str.Split(',');\n            int[] intval = new int[value.Length];\n            for (int i = 0; i < value.Length; ++i)\n            {\n                bool re = int.TryParse(value[i], out intval[i]);\n                if (re) // Ensure successful parsing\n                {\n                    Console.WriteLine(intval[i]);\n                }\n                else\n                {\n                    Console.WriteLine($"Unable to parse '{value[i]}' as an integer.");\n                }\n            }\n        }\n        str = Console.ReadLine();\n    }\n}