using System;\nusing System.Windows.Media;\n\nclass MediaPlayerExample\n{\n    private MediaPlayer _mediaPlayer = new MediaPlayer();\n\n    public MediaPlayerExample()\n    {\n        // Load a media file\n        _mediaPlayer.Open(new Uri("yourSong.wma"));\n\n        // Event handlers for various mediaPlayer events\n        _mediaPlayer.MediaOpened += OnMediaOpened;\n        _mediaPlayer.PlaybackStarted += OnPlaybackStarted;\n        _mediaPlayer.CurrentStateChanged += OnCurrentStateChanged;\n\n        // Start playing the media\n        _mediaPlayer.Play();\n\n        // Periodically check the position of the media being played\n        System.Timers.Timer timer = new System.Timers.Timer(1000); // Check every second\n        timer.Elapsed += OnTimedEvent;\n        timer.AutoReset = true;\n        timer.Enabled = true;\n    }\n\n    private void OnMediaOpened(object sender, RoutedEventArgs e)\n    {\n        Console.WriteLine("Media is opened.");\n    }\n\n    private void OnPlaybackStarted(object sender, RoutedEventArgs e)\n    {\n        Console.WriteLine("Playback started.");\n    }\n\n    private void OnCurrentStateChanged(object sender, System.EventArgs e)\n    {\n        string state = (_mediaPlayer.CurrentState == MediaState.Playing) ? "Playing" : (_mediaPlayer.CurrentState == MediaState.Paused) ? "Paused" : (_mediaPlayer.CurrentState == MediaState.Stopped) ? "Stopped" : "Unknown";\n        Console.WriteLine($"Current media state is: {state}");\n    }\n\n    private void OnTimedEvent(Object source, System.Timers.ElapsedEventArgs e)\n    {\n        if (_mediaPlayer.CurrentState == MediaState.Playing)\n        {\n            TimeSpan currentPosition = _mediaPlayer.Position;\n            Console.WriteLine($"Playing for {currentPosition.Seconds} seconds.");\n        }\n    }\n}