using System;\nusing System.IO;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.parser;\nusing Microsoft.Office.Interop.Word;\n\npublic class DocumentExtractor\n{\n    public string ExtractTextFromPdf(string pdfPath)\n    {\n        using (PdfReader reader = new PdfReader(pdfPath))\n        {\n            StringBuilder text = new StringBuilder();\n            for (int i = 1; i <= reader.NumberOfPages; i++)\n            {\n                text.Append(PdfTextExtractor.GetTextFromPage(reader, i));\n            }\n            return text.ToString();\n        }\n    }\n\n    public string ExtractTextFromDoc(string docPath)\n    {\n        Application application = new Application();\n        Document document = null;\n        try\n        {\n            document = application.Documents.Open(docPath);\n\n            string content = document.Content.Text;\n            return content;\n        }\n        finally\n        {\n            if (document != null) document.Close(false);\n            application.Quit();\n        }\n    }\n\n    public void StoreTextInDatabase(string text, int fileId)\n    {\n        // Implement your database logic here to store the extracted text along with the file ID\n        // Use SqlConnection and SqlCommand for interaction with SQL Server\n    }\n}