// Nullable type usage in C#\nint? nullableInt = null;\n\n// Using DBNull.Value with ADO.NET\nusing (SqlConnection conn = new SqlConnection(connectionString))\n{\n    conn.Open();\n    SqlCommand cmd = new SqlCommand("INSERT INTO People(Name, Age) VALUES (@Name, @Age)", conn);\n    \n    // Using DBNull for a database operation where age might not be present\n    cmd.Parameters.AddWithValue("@Name", "John");\n    if (nullableInt.HasValue)\n    {\n        cmd.Parameters.AddWithValue("@Age", nullableInt.Value);\n    }\n    else\n    {\n        cmd.Parameters.AddWithValue("@Age", DBNull.Value); // Correct way to insert null in the database\n    }\n    \n    cmd.ExecuteNonQuery();\n}