using System.IO;\nusing System.Drawing;\nusing SkiaSharp;\nusing SkiaSharp.TextBlocks;\nusing SkiaSharp.HarfBuzzSharp;\nusing PdfSharp.Pdf;\nusing PdfSharp.Drawing;\n\npublic void ConvertHtmlToPdf(string htmlContent, string outputPath)\n{\n    // Render HTML to an image using HtmlRendererCore\n    var renderedBitmap = HtmlRenderer.RenderToImage(htmlContent);\n\n    // Save the rendered bitmap as a temporary file\n    string tempPath = Path.GetTempFileName();\n    renderedBitmap.Save(tempPath);\n\n    // Create a new PDF document and add the page with the rendered image using PdfSharp\n    var pdfDoc = new PdfDocument();\n    var pdfPage = pdfDoc.AddPage();\n    XGraphics gfx = XGraphics.FromPdfPage(pdfPage);\n    gfx.DrawImage(XImage.FromFile(tempPath), 0, 0);\n\n    // Save the PDF document\n    pdfDoc.Save(outputPath);\n    pdfDoc.Close();\n\n    // Clean up the temporary file\n    File.Delete(tempPath);\n}