static void Main(string[] args)\n{\n    string directoryPath = "C:\\TestDir1";\n\n    // Get the current security settings for the directory.\n    DirectorySecurity dirSecurity = Directory.GetAccessControl(directoryPath);\n\n    // Add a new access rule to allow write access to ASPNET.\n    FileSystemAccessRule writeAce = \n        new FileSystemAccessRule("ASPNET", \n                                 FileSystemRights.Write, \n                                 InheritanceFlags.ContainerInherit | InheritanceFlags.ObjectInherit, \n                                 PropagationFlags.NoPropagateInherit, \n                                 AccessControlType.Allow);\n\n    dirSecurity.AddAccessRule(writeAce);\n\n    // Apply the modified security settings.\n    Directory.SetAccessControl(directoryPath, dirSecurity);\n}