string file = "path_to_your_existing_file.xml"; // specify the real path to your file\nstring tmpFile = Path.GetTempFileName();\n\ntry\n{\n    using (var writer = XmlWriter.Create(tmpFile))\n    {\n        writer.WriteStartElement("root");\n        for (int i = 0; i < 100; i++)\n        {\n            writer.WriteElementString("test", null, \n                "All work and no play makes Jack a dull boy");\n        }\n        writer.WriteEndElement();\n    }\n\n    // Ensure the writer is fully closed before attempting to manipulate files\n    File.Replace(tmpFile, file, null);\n}\ncatch (Exception ex)\n{\n    // Handle possible exceptions here\n    Console.WriteLine("An error occurred: " + ex.Message);\n    // Optionally delete the temporary file if something goes wrong\n    if (File.Exists(tmpFile))\n    {\n        File.Delete(tmpFile);\n    }\n}\nfinally\n{\n    // Clean up any remaining resources, though using statements should handle this\n    // This block is more for general cleanup if needed\n}