public class EventContext : DbContext\n{\n    public DbSet<Event> Events { get; set; }\n\n    protected override void OnModelCreating(DbModelBuilder modelBuilder)\n    {\n        modelBuilder.Entity<Shift>()\n            .Map(m => m.ToTable("Events"))\n            .MapToStoredProcedures();\n\n        modelBuilder.Entity<StaffTimeOff>()\n            .Map(m => m.ToTable("Events"))\n            .MapToStoredProcedures();\n            \n        // Add other derived classes here\n    }\n}\n\npublic abstract class Event\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public DateTime Date { get; set; }\n    public int Discriminator { get; set; } // Used by EF to identify type\n}\n\npublic abstract class ShiftEvent : Event {}\npublic abstract class StaffEvent : Event {}\n\npublic class Shift : ShiftEvent { /* properties */ }\npublic class StaffTimeOff : StaffEvent { /* properties */ }