using System.Diagnostics;\n\npublic void InstallDatabase()\n{\n    string connectionString = "Data Source=YourDataSource;Initial Catalog=master;Integrated Security=True";\n    string sqlCmdPath = @"C:\Program Files\Microsoft SQL Server\90\Tools\binn\SQLCMD.exe"; // Adjust path as necessary\n    string scriptFile = @"setup.sql";\n\n    Process process = new Process();\n    process.StartInfo.FileName = sqlCmdPath;\n    process.StartInfo.Arguments = $"-S YourDataSource -d master -i \"{scriptFile}\"";\n    process.StartInfo.UseShellExecute = false; // Required for redirecting input/output/error streams\n    process.StartInfo.RedirectStandardError = true;\n    process.StartInfo.RedirectStandardOutput = true;\n\n    try\n    {\n        process.Start();\n        string output = process.StandardOutput.ReadToEnd();\n        string errors = process.StandardError.ReadToEnd();\n\n        process.WaitForExit();\n\n        if (!string.IsNullOrEmpty(errors))\n            throw new Exception("SQLCMD Error: " + errors);\n\n        // Optionally log the output for debugging\n        // Console.WriteLine(output);\n    }\n    catch (Exception ex)\n    {\n        throw new Exception("Failed to install database.", ex);\n    }\n}