using Renci.SshNet;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var sftp = new SftpClient("hostname", "username", "password");\n        try\n        {\n            sftp.Connect();\n            // Upload a file\n            using (var fileStream = System.IO.File.OpenRead(@"C:\localfile.txt"))\n                sftp.UploadFile(fileStream, "/remote/path/file.txt");\n\n            // Download a file\n            using (var fileStream = System.IO.File.Create(@"C:\downloadedfile.txt"))\n                sftp.DownloadFile("/remote/path/file.txt", fileStream);\n        }\n        finally\n        {\n            if (sftp.IsConnected)\n                sftp.Disconnect();\n        }\n    }\n}