using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<List<int>> listOfLists = new List<List<int>>\n        {\n            new List<int> { 1, 2, 3 },\n            new List<int> { 3, 4, 5 },\n            new List<int> { 5, 6, 7 }\n        };\n\n        List<int> uniqueInts = listOfLists\n            .SelectMany(innerList => innerList)\n            .Distinct()\n            .ToList();\n\n        // Output the result\n        Console.WriteLine(string.Join(", ", uniqueInts));\n    }\n}