private void SetBoldWordInRichTextBox(RichTextBox richTextBox)\n{\n    richTextBox.Clear();\n    string text = "This is a sample sentence with a bold word.";\n    richTextBox.Text = text;\n    \n    int startIndex = text.IndexOf("bold");\n    if (startIndex != -1)\n    {\n        richTextBox.Select(startIndex, 4); // Select the word "bold"\n        richTextBox.SelectionFont = new Font(richTextBox.Font, FontStyle.Bold);\n        richTextBox.Select(0, 0); // Remove selection\n    }\n}