protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        PopulateTimeDropdown();\n    }\n}\n\nprivate void PopulateTimeDropdown()\n{\n    TimeSpan startTime = new TimeSpan(8, 0, 0); // 8:00 AM\n    TimeSpan endTime = new TimeSpan(19, 45, 0); // 7:45 PM\n\n    for (TimeSpan time = startTime; time <= endTime; time = time.Add(new TimeSpan(0, 15, 0)))\n    {\n        TimeDropdown.Items.Add(time.ToString(@"h\:mm\tt"));\n    }\n}