protected void MenuButton_Click(object sender, EventArgs e)\n{\n    Button clickedButton = (Button)sender;\n    string controlName = "YourModuleNamespace.";\n\n    switch (clickedButton.CommandName)\n    {\n        case "Videos":\n            controlName += "VideoList.ascx";\n            break;\n        case "Artists":\n            controlName += "ArtistList.ascx";\n            break;\n        case "Genres":\n            controlName += "GenreList.ascx";\n            break;\n        default:\n            return;\n    }\n\n    var control = LoadControl(controlName);\n    mainContentPlaceholder.Controls.Clear();\n    mainContentPlaceholder.Controls.Add(control);\n}