using System;\nusing Renci.SshNet;\n\nclass Program\n{\n    static void Main()\n    {\n        var connectionInfo = new ConnectionInfo("hostname", "username", \n            new PasswordAuthenticationMethod("username", "password"));\n\n        using (var client = new ScpClient(connectionInfo))\n        {\n            try\n            {\n                client.Connect();\n                string localFilePath = @"C:\path\to\local\file.txt";\n                string remoteFilePath = "/path/to/remote/directory/file.txt";\n                client.UploadFile(localFilePath, remoteFilePath);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n            finally\n            {\n                client.Disconnect();\n            }\n        }\n    }\n}