using Microsoft.Win32;\n\npublic void SaveActivationKey(string key)\n{\n    RegistryKey currentUser = Registry.CurrentUser;\n    // Open or create a subkey for your application\n    RegistryKey appKey = currentUser.CreateSubKey(@"Software\MyCompany\MyApp");\n    \n    if (appKey != null)\n    {\n        // Store the activation key as a value in this subkey\n        appKey.SetValue("ActivationKey", key);\n        appKey.Close();\n    }\n}\n\npublic string LoadActivationKey()\n{\n    RegistryKey currentUser = Registry.CurrentUser;\n    RegistryKey appKey = currentUser.OpenSubKey(@"Software\MyCompany\MyApp");\n    \n    if (appKey != null)\n    {\n        // Retrieve the activation key from the subkey\n        string key = appKey.GetValue("ActivationKey", "").ToString();\n        appKey.Close();\n        return key;\n    }\n    \n    return "";\n}