using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseBackupRestore\n{\n    private static string connectionString = "Data Source=YourServer;Initial Catalog=YourDatabase;Integrated Security=True";\n\n    public void BackupDatabase(string databaseName, string backupFilePath)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            string command = $"BACKUP DATABASE [{databaseName}] TO DISK = N'{backupFilePath}' WITH NOFORMAT, INIT, NAME = '{databaseName}-Full Database Backup', SKIP, NOREWIND, NOUNLOAD, STATS = 10";\n            \n            using (SqlCommand cmd = new SqlCommand(command, connection))\n            {\n                try\n                {\n                    cmd.ExecuteNonQuery();\n                    Console.WriteLine("Backup successful.");\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine($"Error during backup: {ex.Message}");\n                }\n            }\n        }\n    }\n}