private void UpdateButtonStates()\n{\n    // Enable/Disable Next Button\n    btnNext.Enabled = (CurrentPos < TotalCount - 1);\n    \n    // Enable/Disable Previous Button\n    btnPrevious.Enabled = (CurrentPos > 0);\n}\n\nprivate void LoadRowData(int position)\n{\n    // Ensure the position is within the valid range\n    if (position >= 0 && position < TotalCount)\n    {\n        DataRow row = dataTable.Rows[position];\n        \n        // Display data from the current row, for example:\n        txtSomeField.Text = row["ColumnName"].ToString();\n        \n        CurrentPos = position;\n        UpdateButtonStates();\n    }\n}\n\nprivate void btnNext_Click(object sender, EventArgs e)\n{\n    LoadRowData(CurrentPos + 1);\n}\n\nprivate void btnPrevious_Click(object sender, EventArgs e)\n{\n    LoadRowData(CurrentPos - 1);\n}