using System;\nusing System.Collections.Generic;\nusing Db4objects.Db4o;\n\npublic static void PrintAllPilots(Type objectType, string pathToDb)\n{\n    IObjectContainer db = null;\n    try\n    {\n        db = Db4oFactory.OpenFile(pathToDb);\n        IObjectSet result = db.QueryByExample(Activator.CreateInstance(objectType));\n        ListResult(result);\n    }\n    finally\n    {\n        if (db != null && !db.IsClosed())\n        {\n            db.Close();\n        }\n    }\n}\n\nprivate static void ListResult(IObjectSet results)\n{\n    foreach (var item in results)\n    {\n        Console.WriteLine(item);\n    }\n}