using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class NotificationApp : Form {\n    private NotifyIcon notifyIcon;\n    private Timer timer;\n\n    public NotificationApp() {\n        notifyIcon = new NotifyIcon();\n        notifyIcon.Icon = SystemIcons.Application; // You can change this to your custom icon\n        notifyIcon.Visible = true;\n        notifyIcon.DoubleClick += ShowBalloonTip;\n\n        timer = new Timer { Interval = 5000 }; // Trigger every 5 seconds for demonstration\n        timer.Tick += ShowBalloonTip;\n        timer.Start();\n\n        this.WindowState = FormWindowState.Minimized; // Start minimized to avoid showing the main form\n        this.ShowInTaskbar = false;\n    }\n\n    private void ShowBalloonTip(object sender, EventArgs e) {\n        notifyIcon.BalloonTipTitle = "Notification Title";\n        notifyIcon.BalloonTipText = "This is a notification message.";\n        notifyIcon.BalloonTipIcon = ToolTipIcon.Info;\n        notifyIcon.ShowBalloonTip(5000); // Show balloon tip for 5 seconds\n    }\n\n    [STAThread]\n    public static void Main() {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new NotificationApp());\n    }\n}