using System;\nusing System.Data.SqlClient;\n\npublic class DatabaseBackupRestore\n{\n    // ... previous code ...\n\n    public void RestoreDatabase(string databaseName, string backupFilePath)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            // Ensure the target database is in a state that allows restore.\n            string killConnectionsCommand = $"ALTER DATABASE [{databaseName}] SET SINGLE_USER WITH ROLLBACK IMMEDIATE";\n            \n            using (SqlCommand cmdKill = new SqlCommand(killConnectionsCommand, connection))\n            {\n                cmdKill.ExecuteNonQuery();\n            }\n\n            string command = $"RESTORE DATABASE [{databaseName}] FROM DISK = N'{backupFilePath}' WITH FILE = 1, NOUNLOAD, REPLACE, STATS = 10";\n            \n            using (SqlCommand cmdRestore = new SqlCommand(command, connection))\n            {\n                try\n                {\n                    cmdRestore.ExecuteNonQuery();\n                    string setMultiUserCommand = $"ALTER DATABASE [{databaseName}] SET MULTI_USER";\n                    \n                    using (SqlCommand cmdSetMultiUser = new SqlCommand(setMultiUserCommand, connection))\n                    {\n                        cmdSetMultiUser.ExecuteNonQuery();\n                    }\n                    Console.WriteLine("Restore successful.");\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine($"Error during restore: {ex.Message}");\n                }\n            }\n        }\n    }\n}