using System.Web.Security;\n\npublic static void SeedMembership()\n{\n    string username = "admin";\n    string password = "P@ssw0rd!";\n    string email = "admin@example.com";\n\n    // Check if the user already exists in membership system\n    if (!Membership.UserExists(username))\n    {\n        try\n        {\n            MembershipCreateStatus status;\n            MembershipUser newUser = Membership.CreateUser(username, password, email, null, null, true, out status);\n\n            if (newUser != null)\n            {\n                // Add the user to a role, for example "Admin"\n                Roles.AddUserToRole(newUser.UserName, "Admin");\n                System.Diagnostics.Debug.WriteLine("Seed User created successfully.");\n            }\n            else\n            {\n                switch (status)\n                {\n                    case MembershipCreateStatus.DuplicateUserName:\n                        System.Diagnostics.Debug.WriteLine("Username already exists. Please enter a different user name.");\n                        break;\n                    case MembershipCreateStatus.InvalidPassword:\n                        System.Diagnostics.Debug.WriteLine("The password provided is invalid. Please enter a valid password value.");\n                        break;\n                    default:\n                        System.Diagnostics.Debug.WriteLine($"Error: {status}");\n                        break;\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            System.Diagnostics.Debug.WriteLine($"An exception occurred: {ex.Message}");\n        }\n    }\n}