bool IsPerfectSquare(long input)\n{\n    if (input < 0) return false;\n\n    long left = 0, right = input;\n    while (left <= right)\n    {\n        long mid = left + (right - left) / 2;\n        long square = mid * mid;\n\n        if (square == input)\n            return true;\n        else if (square < input)\n            left = mid + 1;\n        else\n            right = mid - 1;\n    }\n\n    return false;\n}