using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string sql = @"CREATE TABLE [dbo].[User]( [UserId] [int] IDENTITY(1,1) NOT NULL, \n[FirstName] [varchar](50) COLLATE SQL_Latin1_General_CP1_CI_AS NOT NULL, [MiddleName]\n[varchar](50) COLLATE SQL_Latin1_General_CP1_CI_A ";\n\n        string pattern = @"\[(?<field>\w+)\]\s*\[(?<type>\w+)";\n        Regex regex = new Regex(pattern);\n\n        foreach (Match match in regex.Matches(sql))\n        {\n            Console.WriteLine($"{match.Groups["field"].Value} {match.Groups["type"].Value}");\n        }\n    }\n}