using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        const int N = 6000000;\n        int[] array = new int[N];\n        List<int> list = new List<int>(array);\n\n        // Time Array iteration\n        Stopwatch stopwatchArray = Stopwatch.StartNew();\n        for (int i = 0; i < array.Length; i++)\n        {\n            var _ = array[i]; // Simulate using the array element\n        }\n        stopwatchArray.Stop();\n\n        // Time List iteration\n        Stopwatch stopwatchList = Stopwatch.StartNew();\n        for (int i = 0; i < list.Count; i++)\n        {\n            var _ = list[i]; // Simulate using the list element\n        }\n        stopwatchList.Stop();\n\n        Console.WriteLine("Array Iteration Time: " + stopwatchArray.ElapsedTicks + " ticks");\n        Console.WriteLine("List Iteration Time: " + stopwatchList.ElapsedTicks + " ticks");\n    }\n}