using System;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class TcpExample {\n    public static void Main() {\n        using (TcpClient client = new TcpClient("example.com", 80)) {\n            NetworkStream stream = client.GetStream();\n            byte[] dataToSend = Encoding.ASCII.GetBytes("GET / HTTP/1.1\r\nHost: example.com\r\nConnection: close\r\n\r\n");\n            stream.Write(dataToSend, 0, dataToSend.Length);\n            int bytesReceived;\n            do {\n                byte[] buffer = new byte[256];\n                bytesReceived = stream.Read(buffer, 0, buffer.Length);\n                Console.Write(Encoding.ASCII.GetString(buffer, 0, bytesReceived));\n            } while (bytesReceived != 0);\n        }\n    }\n}