public class LogRun\n{\n    public virtual int Id { get; private set; }\n    public virtual DateTime StartTime { get; set; }\n    public virtual DateTime EndTime { get; set; }\n\n    // Initialize the list directly in the property declaration\n    private IList<Log> _logMessages = new List<Log>();\n    public virtual IList<Log> LogMessages \n    {\n        get => _logMessages;\n        protected set => _logMessages = value ?? throw new ArgumentNullException(nameof(value));\n    }\n\n    public virtual int LogMessageCount => LogMessages.Count;\n\n    public LogRun()\n    {\n        // Constructor can remain empty since the list is already initialized\n    }\n}