using System;\nusing System.Globalization;\nusing System.IO;\n\npublic class ScriptDateFormatter\n{\n    public static void ChangeScriptDateFormat(string filePath)\n    {\n        string[] lines = File.ReadAllLines(filePath);\n        for (int i = 0; i < lines.Length; i++)\n        {\n            if (lines[i].Contains("Script Date:"))\n            {\n                // Extract date part from the line\n                string datePart = lines[i].Split(':')[1].Trim();\n                \n                // Parse the US date format\n                DateTime scriptDate;\n                if (DateTime.TryParseExact(datePart, "MM/dd/yyyy HH:mm:ss", CultureInfo.InvariantCulture, \n                                                           DateTimeStyles.None, out scriptDate))\n                {\n                    // Format it to Australian date format\n                    string newDate = scriptDate.ToString("dd/MM/yyyy HH:mm:ss", new CultureInfo("en-AU"));\n                    \n                    // Replace the old with the new formatted date\n                    lines[i] = lines[i].Replace(datePart, newDate);\n                }\n            }\n        }\n\n        // Write back to the file\n        File.WriteAllLines(filePath, lines);\n    }\n}\n\n// Usage:\nScriptDateFormatter.ChangeScriptDateFormat(@"C:\path\to\your\script.sql");