public abstract class SynchronizedBase\n{\n    private readonly object _locker = new object();\n\n    protected void SetValue<T>(ref T field, T value)\n    {\n        lock (_locker)\n        {\n            field = value;\n        }\n    }\n\n    protected T GetValue<T>(T field) => field;\n}\n\npublic class MyClass : SynchronizedBase\n{\n    private int _x;\n\n    public int X\n    {\n        get => GetValue(_x);\n        set => SetValue(ref _x, value);\n    }\n}