// Omitting initialization and increment/decrement\nfor (; true; )\n{\n    // This will run indefinitely until a break statement or other exit condition is encountered\n    Console.WriteLine("Infinite loop");\n}\n\n// Omitting only the condition makes it an infinite loop equivalent to for(;;)\nfor (int i = 0; ; i++)\n{\n    // Loop with initialization and increment but no condition check, will run indefinitely until a break statement or other exit condition is encountered\n    Console.WriteLine("Loop running: " + i);\n}\n\n// Omitting all parts makes it an infinite loop equivalent to for(;;)\nfor (;;)\n{\n    // Infinite loop\n    Console.WriteLine("Infinite loop");\n}