using (var context = new YourDataContext())\n{\n    var columnSize = (\n        from column in context.ExecuteQuery<int>(\n            "SELECT CHARACTER_MAXIMUM_LENGTH FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'YourTableName' AND COLUMN_NAME = 'YourColumnName'")\n        select column).FirstOrDefault();\n\n    Console.WriteLine($"The size of the column is: {columnSize}");\n}