protected void Page_Load(object sender, EventArgs e)\n{\n    if (Session["login"] != null && Session["login"].ToString() == "1")\n    {\n        // The user is authenticated\n        if (!IsPostBack)\n        {\n            LoadData(); // Only load data on the initial page load, not postbacks\n        }\n    }\n    else\n    {\n        Response.Redirect("login.aspx", true); // Redirect to login page if not authenticated\n    }\n}