public abstract class ModelBase\n{\n    public bool IsValid\n    {\n        get\n        {\n            return !GetRuleViolations().Any();\n        }\n    }\n\n    public virtual void Validate(ChangeAction action)\n    {\n        if (!IsValid) \n            throw new ApplicationException("Rule violations prevent saving");\n    }\n\n    public abstract IEnumerable<RuleViolation> GetRuleViolations();\n}\n\npublic partial class Foo : ModelBase\n{\n    partial void OnValidate(ChangeAction action)\n    {\n        Validate(action);\n    }\n\n    public override IEnumerable<RuleViolation> GetRuleViolations()\n    {\n        // Add your validation logic here\n        yield break;\n    }\n}\n\npublic partial class Bar : ModelBase\n{\n    partial void OnValidate(ChangeAction action)\n    {\n        Validate(action);\n    }\n\n    public override IEnumerable<RuleViolation> GetRuleViolations()\n    {\n        // Add your validation logic here\n        yield break;\n    }\n}